actor_movies.each do | actor_name, movies |
   actor = Actor.find_by( name: actor_name )

   movies.each do | title, year, cover, plot, genres |
      movie = Movie.create( title:title, actor_id: actor.id, year: year, cover: cover, plot: plot )

      genres.split( ", ").each do |genre_name|
         if genre = Genre.find_by( name: genre_name )
         else
            genre = Genre.create( name: genre_name )
         end

         GenresMovies.create( movie: movie, genre: genre )
      end
   end
end
